package javaSyntaxHomework;import java.util.Scanner;public class P04_TheSmallestOfThreeNums {	public static void main(String[] args) {		Scanner input = new Scanner(System.in);		System.out.println("Enter the first number:");		double firstNum = Double.parseDouble(input.nextLine());		System.out.println("Enter the second number:");		double secondNum = Double.parseDouble(input.nextLine());		System.out.println("Enter the third number:");		double thirdNum = Double.parseDouble(input.nextLine());				double smallest = firstNum;		if (secondNum < smallest) {			smallest = secondNum;					}		if (thirdNum < smallest) {			smallest = thirdNum;		}		System.out.println(smallest);	}}